(101, 'Principiile zborului', 1, ' Finetea aerodinamica este:', ' raportul dintre Cx si Cz', ' raportul dintre vectorul viteza si greutate', ' descrie calitatea suprafetei portante', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:45'),
(102, 'Principiile zborului', 2, ' Finetea aerodinamica este influentata de :', ' lungimea suspantelor', ' clasa de omologare a parapantei', ' directia vantului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:45'),
(103, 'Principiile zborului', 3, ' Forta portanta se genereaza:', ' 2/3 pe extrados', ' 2/3 pe intrados', ' ½ pe extrados si ½ pe intrados', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:45'),
(104, 'Principiile zborului', 4, ' Viteza minima de infundare este influentata de :', ' viteza de zbor', ' lungimea suspantelor', ' directia si viteza vantului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:45'),
(105, 'Principiile zborului', 5, ' Unghiul de incidenta este dat de :', ' coarda profilului si directia curentului  de aer relativ', ' extradosul aripii si orizontala', ' coarda profilului si orizontala', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:45'),
(106, 'Principiile zborului', 6, ' Cand unghiul de incidenta creste:', ' creste portanta, scade viteza', ' creste portanta, creste viteza', ' scade portanta, scade viteza', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:45'),
(107, 'Principiile zborului', 7, ' Desprinderea stratului limita apare cand:', ' creste unghiul de incidenta pana la unghi critic', ' scade unghiul de incidenta la o valoare inferioara unghiului critic', ' se elibereaza comenzile', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:45'),
(108, 'Principiile zborului', 8, ' Forta portanta este influentata de :', ' unghiul de incidenta', ' viteza vantului', ' directia vantului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:45'),
(109, 'Principiile zborului', 9, ' Unghiul de atac critic duce la:', ' desprinderea stratului limita', ' cresterea portantei la nivel maxim', ' lipirea fileurilor de aer de extrados', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:45'),
(110, 'Principiile zborului', 10, ' Centrul de presiune este:', ' mobil sau fix functie de tipul profilului aerodinamic', ' fix', ' mobil in toate cazurile', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:45'),
(111, 'Principiile zborului', 11, ' Angajarea apare :', ' la viteza maxima de zbor', ' la atingerea vitezei minime de infundare', ' la atingerea unghiului de incidenta critic', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 15:48:46'),
(112, 'Principiile zborului', 12, ' Dupa legea lui Bernoulli:', ' cand viteza creste, scade presiunea', ' cand viteza creste, creste si presiunea', ' nu are legatura cu viteza de curgere a fluidelor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:46'),
(113, 'Principiile zborului', 13, ' Din polara putem deduce:', ' viteza minima de infundare', ' greutatea maxima de zbor', ' unghiul maxim de inclinare in zbor normal', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:46'),
(114, 'Principiile zborului', 14, ' Alungirea aripii este:', ' raportul dintre coarda medie si anvergura', 'raportul dintre profunzimea maxima a aripii si anvergura', ' raportul dintre grosimea maxima relativa a profilului si anvergura', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:46'),
(115, 'Principiile zborului', 15, ' Rezistenta indusa este data de :', ' turbioanele de la capetele aripii', ' rezistenta la inaintare a intregii aripii plus cea a suspantelor', ' rezistenta la inaintare generata de voalura', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:46'),
(116, 'Principiile zborului', 16, ' In cazul aripii eliptice, la aripa fara torsiune geometrica, stratul limita se desprinde incepand cu:', ' capetele de aripa', ' zona centrala a aripii', ' paralel cu bordul de atac, in acelasi timp de-a lungul anvergurii', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:46'),
(117, 'Principiile zborului', 17, ' Aripa de parapanta prezinta torsiune geometrica:', ' da', ' nu', ' torsiunea geometrica nu are legatura cu mecanica zborului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:46'),
(118, 'Principiile zborului', 18, ' Coarda profilului defineste:', ' linia ce uneste punctele extreme ale profilului, din zona bordului de atac si din zona bordului de fuga', ' linia ce uneste bordul de fuga cu directia de curgere a fileurilor de aer', ' curbura liniei extradosului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:46'),
(119, 'Principiile zborului', 19, ' Care dintre urmatoarele afirmatii este adevarata:', ' la cresterea unghiului de incidenta scade viteza', ' la cresterea unghiului de incidenta scade portanta', ' la cresterea unghiului creste viteza', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:48:46'),
(120, 'Principiile zborului', 20, ' Care dintre urmatoarele afirmatii este adevarata:', ' rezistenta indusa este influentata de inaltimea de zbor', ' rezistenta indusa are legatura cu rezistenta la rupere a suspantelor', ' rezistenta indusa nu are lagatura cu zborul', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:15:49'),
(121, 'Principiile zborului', 21, ' Care dintre urmatoarele afirmatii este adevarata:', ' rezistenta la inaintare este direct proportionala cu patratul vitezei', ' rezistenta la inaintare este invers proportinala cu patratul vitezei', ' rezistenta la inaintare este direct proportionala doar cu aria sectiunii', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:17:20'),
(122, 'Principiile zborului', 22, ' Teoria care sta la baza zborului cu parapanta face parte din:', ' domeniul fluidelor compresibile', ' domeniul fluidelor incompresibile', ' nu are lagatura cu compresibilitatea', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:17:20'),
(123, 'Principiile zborului', 23, ' Care dintre urmatoarele nu face parte din domeniul aerodinamicii:', ' rezistenta ohmica', ' rezistenta indusa', ' rezistenta parazita', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:17:20'),
(124, 'Principiile zborului', 24, ' Viteza de curgere a fileurilor de aer pe un profil aerodinamic este:', ' mai mare pe extrados decat pe intrados', ' mai mare pe intrados decat pe extrados', ' egala intrados cu extrados', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:17:20'),
(125, 'Principiile zborului', 25, ' Turbioanele terminale responsabile pentru rezistenta indusa apar:', ' pe capetele aripii', ' in spatele aripii (siaj)', ' in spate si sub aripa', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:17:20'),
(126, 'Principiile zborului', 26, ' Centrul de presiune este:', ' fix sau mobil functie de tipul profilului', ' mobil', ' nu exista aceasta notiune in aviatie', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(127, 'Principiile zborului', 27, ' Centrul de presiune se regaseste intotdeauna  la nivelul:', ' in jumatatea dinainte a aripii', ' bordului de fuga in zona centrala a aripii', ' bordului de atac al aripii', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(128, 'Principiile zborului', 28, ' In care din urmatoarele cazuri viteza de angajare creste:', ' creste incarcarea alara', ' scade viteza de zbor fata de sol', ' micsoram numarul de suspante', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(129, 'Principiile zborului', 29, ' La atingerea unghiului de atac critic:', ' portanta scade brusc, iar rezistenta la inaintare creste brusc', ' portanta creste proportional cu scaderea vitezei', ' portanta creste proportional cu viteza', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(130, 'Principiile zborului', 30, ' Desprinderea stratului limita apare:', ' la atingerea unghiului de atac critic', ' la atingerea vitezei minime de zbor', ' la atingerea vitezei maxime de zbor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(131, 'Principiile zborului', 31, ' Conform teoremei lui Bernoulli atunci cand sectiunea unui tub se micsoreaza:', ' viteza de curgere creste iar presiunea statica scade', ' viteza de curgere creste iar presiunea statica creste', ' viteza de curgere scade iar presiunea statica creste', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(132, 'Principiile zborului', 32, ' Din polara vitezelor putem deduce:', ' viteza minima de infundare, viteza la finete maxima, viteza maxima de zbor', ' viteza maxima in viraj', ' viteza de zbor in functie de altitudine', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(133, 'Principiile zborului', 33, ' Infundarea minima se obtine :', ' la o viteza inferioara vitezei la finete maxima', ' la o viteza egala cu viteza la finete maxima', ' la o viteza mai mare decat viteza la finete maxima', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(134, 'Principiile zborului', 34, ' Finetea parapantei reprezinta:', ' unghiul de panta in zbor exprimat printr-un raport', ' calitatea finisajului extradosului si intradosului', ' coeficientul de frecare cu aerul al intregului ansamblu: voalura, suspante, pilot', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(135, 'Principiile zborului', 35, ' Finetea aerodinamica a parapantei este influentata de :', ' valoarea rezistentei la inaintare', 'valoarea vitezei maxime de zbor', ' valoarea vitezei de angajare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(136, 'Principiile zborului', 36, ' Care dintre urmatoarele afirmatii este adevarata:', ' presiunea dinamica este influentata de viteza', ' presiunea statica este intotdeauna egala cu presiunea dinamica', ' presiunea dinamica este influentata de altitudine', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(137, 'Principiile zborului', 37, ' Pentru a imbunatati finetea unei parapante putem sa:', ' folosim o seleta profilata (cocon)', ' marim suprafata portanta', ' sa micsoram suprafata portanta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(138, 'Principiile zborului', 38, ' In zbor planat stabilizat forta portanta este:', ' mai mica decat greutatea', ' mai mare decat greutatea', ' egala cu greutatea', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(139, 'Principiile zborului', 39, ' Angajarea in limita de viteza apare ca urmare:', ' ruperii fileurilor de aer de pe extrados', ' ruperii fileurilor de aer de pe intrados', ' zborului la viteza minima', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(140, 'Principiile zborului', 40, ' In timpul procedurii de decolare unghiul de atac este:', ' variabil', ' fix', ' variaza doar daca se actioneaza acceleratorul', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:33'),
(141, 'Principiile zborului', 41, ' Care din urmatoarele au drept consecinta cresterea finetei unei parapante:', ' micsorarea numarului de suspante', ' scaderea incarcarii alare', ' cresterea greutatii totale', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:34'),
(142, 'Principiile zborului', 42, ' Profilul aripii este dat de :', ' membranele intercheson', ' proiectia aripii pe un plan orizontal', ' suprafata aripii desfasurata', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:34'),
(143, 'Principiile zborului', 43, ' Viteza minima de infundare este:', ' mai mica decat viteza la finete maxima', ' mai mare decat viteza la finete', ' egala cu viteza la finete', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:34'),
(144, 'Principiile zborului', 44, ' Regimurile de zbor ale parapantei sunt:', ' viteza minima, viteza maxima, viteza accelerata', ' viteza la infundare minima, viteza la finete maxima, viteza maxima', ' viteza de angajare, viteza maxima, viteza la finete', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 15:55:34'),
(145, 'Principiile zborului', 45, ' Stratul limita este:', ' stratul de aer din imediata vecinatate a aeronavei în care viteza creste de la zero la viteza de zbor', ' stratul de aer format când avionul este în limita de viteza', ' stratul de aer care inconjoara profilul aerodinamic la unghi de incidenta critic', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:19:58'),
(146, 'Principiile zborului', 46, ' Grosimea unui profil aerodinamic este:', ' cea mai mare distanta dintre extrados si intrados', ' media grosimilor sectiunilor profilului', ' constanta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:19:58'),
(147, 'Principiile zborului', 47, ' Rezultanta fortelor aerodinamice profilului are punctul de aplicatie în:', ' centrul de presiune', ' centrul geometric al aripii', ' centrul de greutate', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:19:58'),
(148, 'Principiile zborului', 48, ' In zbor orizontal centrul de greutate este situat:', ' sub centrul de presiune', ' deasupra centrului de presiune', ' coincide cu centrul de presiune', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:19:58'),
(149, 'Principiile zborului', 49, ' Cantitativ forta portanta este :', ' variabila in functie de viteza de zbor', ' constanta in functie de viteza de zbor', ' constanta in toate cazurile cu exceptia angajarii', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:19:58'),
(150, 'Principiile zborului', 50, ' Lungimea suspantelor poate influenta finetea parapantei:', ' da', ' nu', ' numai o data cu cresterea incarcarii alare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:19:58'),
(151, 'Principiile zborului', 51, ' Incarcarea alara se refera la :', ' raportul dintre greutatea totala in zbor si suprafata portanta', ' greutatea mazima in zbor', ' greutatea maxima suspendata', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:05'),
(152, 'Principiile zborului', 52, ' Cresterea incarcarii alare modifica performantele parapantei:', ' da', ' nu', ' incarcarea alara nu are legatura cu domeniul zborului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:05'),
(153, 'Principiile zborului', 53, ' La actionarea in jos a comenzilor unghiul de incidenta:', ' creste', ' scade', ' ramane constant', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:05'),
(154, 'Principiile zborului', 54, ' La cresterea vitezei de scurgere a fileurilor de aer pe extrados:', ' scade presiunea statica', ' creste presiunea statica', ' presiunea statica ramane constanta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:05'),
(155, 'Principiile zborului', 55, ' Care dintre urmatoarele fenomene nu au legatura cu aerodinamica:', ' legea distributiei maselor', ' legea lui Bernoulli,', ' efectul Coanda', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:05'),
(156, 'Principiile zborului', 56, ' Efectul Coanda este influentat de unghiul de incidenta?', ' da', ' nu', ' doar la unghiuri de incidenta subcritice', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:05'),
(157, 'Principiile zborului', 57, ' La micsorarea unghiului de incidenta :', ' viteza de zbor creste', ' viteza de zbor scade', ' viteza de zbor ramane constanta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:05'),
(158, 'Principiile zborului', 58, ' La cresterea unghiului de incidenta se poate ajunge la angajare numai cand:', ' se atinge unghiul critic', ' se atinge viteza minima de infundare', ' se atinge viteza la finete', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:05'),
(159, 'Principiile zborului', 59, ' Fata de zborul orizontal, in timpul virajului,  viteza de angajare :', ' creste', ' scade', ' ramane neschimbata', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:05'),
(160, 'Principiile zborului', 60, ' Forta centrifuga actioneaza toteauna:', ' spre exteriorul virajului', ' spre interiorul virajului', ' spre in sus', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:05'),
(161, 'Principiile zborului', 61, ' Cele 4 forte care actioneaza pe un profil aerodinamic sunt:', ' greutatea, portanta, rezistenta la inaintare, viteza', ' greutatea, portanta, forta centrifuga viteza', ' portanta, greutatea, forta centrifuga, rezistenta la inaintare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:05'),
(162, 'Principiile zborului', 62, ' Fortei portante i se opune:', ' greutatea', ' viteza', ' rezistenta la inaintare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:06'),
(163, 'Principiile zborului', 63, ' Rugozitatea unei suprafete portante duce la:', ' cresterea rezistentei la inaintare', ' cresterea vitezei de zbor', ' scaderea sarcinii maxime admise', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:06'),
(164, 'Principiile zborului', 64, ' Care dintre urmatoarele afirmatii este adevarata:', ' finetea depinde de viteza de zbor', ' finetea maxima se obtine la viteza de angajare (minima de zbor)', ' finetea maxima se obtine la viteza maxima de zbor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:06'),
(165, 'Principiile zborului', 65, ' Care dintre urmatoarele afirmatii este adevarata:', ' viteza minima de infundare si finetea sunt caracteristici importante ale parapantei', ' viteza de infundare a parapantei este indiferenta de viteza de zbor', ' viteza minima de infundare  a parapantei se obtine la viteza minima de zbor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:06'),
(166, 'Principiile zborului', 66, ' Presiunea dinamica masurata in interiorul aripii variaza:', ' in functie de viteza de zbor', ' in functie alungirea parapantei', ' in functie de grosimea maxima relativa a profilului parapantei', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:06'),
(167, 'Principiile zborului', 67, ' Trompa lui Venturi demonstreaza:', ' legea Bernoulli', ' efectul Coanda', ' aparitia rezistentei la inaintare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:06'),
(168, 'Principiile zborului', 68, ' Polara aripii de parapanta descrie:', ' caracteristicile de zbor ale parapantei', ' forma aripii in plan', ' forma sheletului profilului aerodinamic', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 16:03:06'),
(169, 'Principiile zborului', 69, ' La parapanta, relatia de ordonare intre Vangajare, Vmax, Vfinete-maxima si Vinfundare-minima este:', ' Vinfundare-minima < Vangajare <  Vfinete-maxima < Vmax', ' Vangajare <  Vinfundare-minima < Vfinete-maxima < Vmax', ' Vangajare <  Vfinete-maxima < Vinfundare-minima < Vmax', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 16:03:06'),
(170, 'Principiile zborului', 70, ' In momentul angajarii unei parapante', ' Presiunea in comenzi scade brusc', ' Viteza de inaintare creste brusc', ' Viteza de infundare creste brusc', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,3', '2015-09-27 16:21:56'),
(171, 'Principiile zborului', 71, ' Incarcarea aripii in zbor planat stabilizat este definita ca si', ' Tensiunea din suspante exprimata in Kg/ml', ' Greutatea pilotului plus greutatea seletei si a aparatelor de zbor', ' Raportul dintre greutatea totala in zbor si suprafata aripii', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 16:21:56'),
(172, 'Principiile zborului', 72, ' Ce se intampla daca marim incarcarea pe aripa:', ' creste finetea maxima', ' toate vitezele cresc, inclusiv viteza de angajare', ' viteza de angajare ramine nemodificata deoarece este o caracteristica a profilului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 16:21:56'),
(173, 'Principiile zborului', 73, ' Intr-un viraj strans cu unghi de inclinare accentuat sarcina pe aripa', ' scade', ' creste', ' ramine constanta doarece greutatea sistemului si suprafata aripii nu se modifica', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 16:21:56'),
(174, 'Principiile zborului', 74, ' Rezistenta la inaintare se datoreaza:', ' formei profilului', ' rezistentei induse', ' frecarii aerului cu toate componentele aflate in zbor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2,3', '2015-09-27 16:21:56'),
(175, 'Principiile zborului', 75, ' La parapanta, stabilitatea in jurul axei transversale (axa ce uneste capetele aripii) se datoreaza in special', ' vitezei relativ mici la care zboara', ' profilului aripii', ' efectului de pendul (centrul de greutate se afla mult mai jos decit centrul de presiune)', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2,3', '2015-09-27 16:21:56'),
