(412, 'Meteorologie', 12, ' Unitatea de masura a presiunii atmosferice este:', ' hectopascal;', ' milibari;', ' milimetrul coloana de mercur.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2,3', '2015-09-27 17:28:25'),
(413, 'Meteorologie', 13, ' Masurarea presiunii atmosferice se poate realiza cu ajutorul:', ' hectometrul;', ' variometrul;', ' barometrul.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:28:25'),
(414, 'Meteorologie', 14, ' Presiunea atmosferica scade in altitudine datorita:', ' scaderii densitatii aerului in inaltime;', ' scurtarii coloanei de aer odata cu cresterea inaltimii;', ' scaderii umiditatii.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-27 17:28:25'),
(415, 'Meteorologie', 15, ' Prin definitie, treapta barica reprezinta:', ' distanta pe verticala, in metri, pentru care se inregistreaza o crestere a presiunii atmosferice cu 1 milibar;', ' distanta pe verticala, in metri, pentru care se inregistreaza o descrestere a presiunii atmosferice cu 1 milibar;', ' distanta pe orizontala, in metri, pentru care se inregistreaza o descrestere a presiunii atmosferice cu 1 milibar.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:28:25'),
(416, 'Meteorologie', 16, ' Vantul este miscarea aerului pe:', ' orizontala;', ' verticala;', ' diagonala.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:28:25'),
(417, 'Meteorologie', 17, ' Miscarile verticale si/sau inclinate ale aerului se numesc:', ' curenti;', ' vanturi;', ' rafale.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:28:25'),
(418, 'Meteorologie', 18, ' De ce este provocat vantul:', ' de diferenta de presiune pe verticala;', ' de diferenta de presiune pe diagonala;', ' de diferenta de presiune pe orizontala.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:28:25'),
(419, 'Meteorologie', 19, '  Incalzirea suprafetei terestre deci si a maselor de aer din vecinatatea acestora este:', ' egala;', ' omogena;', ' inegala.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:28:25'),
(420, 'Meteorologie', 20, ' Marimile ce definesc vantul sunt:', ' directia;', ' intensitatea (si viteza)', ' densitatea.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-27 17:28:25'),
(421, 'Meteorologie', 21, ' Prin directia vantului, in meteorologie se intelege:', ' directia inspre care "sufla" (vine) vantul;', ' directia de unde "sufla" (vine) vantul;', ' nordul magnetic.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:30:55'),
(422, 'Meteorologie', 22, ' 1m/s este egal cu:', ' 2,8 km/h;', ' 3,6 km/h;', ' 12km/h.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:30:55'),
(423, 'Meteorologie', 23, ' Liniile care unesc punctele cu aceeasi presiune atmosferica:', ' izometre;', ' izoterme;', ' izobare.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:30:55'),
(424, 'Meteorologie', 24, ' Distanta pe verticala masurata de la nivelul mediu al marii pana la punctul considerat unde se afla aeronava se numeste:', ' altitudine;', ' inaltimea reala;', ' inaltime', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:30:55'),
(425, 'Meteorologie', 25, ' Care din urmatoarele afirmatii sunt corecte:', ' QNH = presiunea redusa la nivelul marii;', ' QFE = presiunea la nivelul pistei;', ' QNE (Std) = presiunea de referinta de 760 mm col Hg sau 1013.25Mb (HPa).', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2,3', '2015-09-27 17:30:55'),
(426, 'Meteorologie', 26, ' Pamantul se incalzeste de la soare printr-un fenomen numit:', ' conductie;', ' convectie;', ' radiatie.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:30:55'),
(427, 'Meteorologie', 27, ' Izotermele reprezinta:', ' liniile ce unesc toate punctele cu aceeasi temperatura;', ' liniile ce unesc toate punctele cu aceeasi presiune;', ' liniile ce unesc toate punctele cu aceeasi altitudine.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:30:55'),
(428, 'Meteorologie', 28, ' Masa calda de aer este obligata sa urce pe panta aerului rece, si prin detenta are loc racirea adiabatica, conditie care determina ajungerea masei de aer la saturatie, fenomen denumit:', ' convectie frontala;', ' convectie orografica;', ' convectie orizontala.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:30:55'),
(429, 'Meteorologie', 29, ' Masa calda de aer este obligata sa urce pe panta montana, si prin detenta are loc racirea adiabatica, conditie care determina ajungerea masei de aer la saturatie, fenomen denumit:', ' convectie frontala;', ' convectie orografica;', ' convectie orizontala.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:30:55'),
(430, 'Meteorologie', 30, ' Variatia temperaturii pentru o diferenta de nivel de 100 m se numeste:', ' gradientul termic vertical;', ' gradientul orizontal;', ' gradientul adiabatic.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:30:55'),
(431, 'Meteorologie', 31, ' Valoarea gradientului termic vertical mediu este:', ' 1&deg;C/100 m;', ' 0,5&deg;C/100 m;', ' 0,65&deg;C/100m.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:35:17'),
(432, 'Meteorologie', 32, ' Spunem despre atmosfera ca este instabila daca:', ' miscarile verticale produse la un moment dat, nu pot sa se dezvolte si deci inceteaza;', ' miscarile verticale care se produc se propaga de la un nivel la altul;', ' curentii ascendenti formati in urma unui impuls oarecare se vor opri din miscarea acensionala.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:35:17'),
(433, 'Meteorologie', 33, ' Spunem despre atmosfera ca este stabila daca:', ' curentii ascendenti formati in urma unui impuls oarecare se vor opri din miscarea acensionala;', ' curentii ascendenti tind sa-si continue miscarea impulsionati de o accelaratie noua;', ' miscarile orizontale produse la un moment dat, nu pot sa se dezvolte si deci inceteaza.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:35:17'),
(434, 'Meteorologie', 34, ' Zona din troposfera in care temperatura creste odata cu cresterea de inaltime se numeste zona de:', ' Inversiune termica;', ' izotermie;', ' condensare.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:35:17'),
(435, 'Meteorologie', 35, ' Izotermia se defineste ca zona:', ' in care temperatura este stationara cu cresterea de inaltime;', ' in care temperatura creste odata cu cresterea de inaltime;', ' in care temperatura scade odata cu cresterea de inaltime.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:35:17'),
(436, 'Meteorologie', 36, ' In cazul in care avem inversiune termica la sol se poate produce:', ' polei;', ' chiciura;', ' ceata.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:35:17'),
(437, 'Meteorologie', 37, ' Care din urmatoarele afirmatii este falsa?', ' o masa de aer cald urca peste o masa de aer rece;', ' o masa de aer rece in miscare disloca o masa de aer cald pe care o obliga sa urce;', ' o masa de aer cald coboara sub o masa de aer rece;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:35:17'),
(438, 'Meteorologie', 38, ' Temperatura standard in aviatie este:', ' 15&deg;C la 760 mm Hg;', ' 10&deg;C la 760 mm Hg;', ' 0&deg;C la 760 mm Hg.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:35:17'),
(439, 'Meteorologie', 39, ' Temperatura punctului de roua este:', ' temperatura la care aerul devine saturat prin racire la o presiune constanta;', ' temperatura la care aerul devine saturat prin racire la o presiune in crestere;', ' temperatura la care aerul devine saturat prin incalzire la o presiune constanta.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:35:17');
INSERT INTO `questions` (`id`, `category`, `category_index`, `text`, `answer_1`, `answer_2`, `answer_3`, `answer_4`, `answer_5`, `answer_6`, `answer_7`, `answer_8`, `answer_9`, `answer_10`, `correct`, `date`) VALUES
(440, 'Meteorologie', 40, ' Ce se intampla cu o masa de aer in urcare datorita scaderii presiunii?', ' aerul se contracta;', ' aerul se dilata;', ' aerul nu-si modifica volumul.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:35:17'),
(441, 'Meteorologie', 41, ' Ce reprezinta nivelul de condensare?', ' Inaltimea la care apar norii;', ' altitudinea la care umezeala relativa devine 100%;', ' inaltimea la care umezeala relativa devine 100%.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:38:07'),
(442, 'Meteorologie', 42, ' In mod obisnuit, ploaia cade din:', ' Nimbostratus;', ' Stratus;', ' Stratocumulus.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:38:07'),
(443, 'Meteorologie', 43, ' Centru de minima presiune caracterizata prin descresterea presiunii catre centrul sistemului se numeste:', ' anticiclon;', ' ciclon;', ' anticiclon, ciclon.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:38:08'),
(444, 'Meteorologie', 44, ' Vantul are o miscare de la exterior spre centru sau in sens invers acelor de ceasornic (pentru emisfera nordica).', ' in cazul ciclonului;', ' in cazul izotermei;', ' in cazul anticiclonului.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:38:08'),
(445, 'Meteorologie', 45, ' Centru de maxima presiune caracterizata prin cresterea presiunii de la exterior spre centrul sistemului se numeste:', ' anticiclon;', ' ciclon;', ' anticiclon, ciclon.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:38:08'),
(446, 'Meteorologie', 46, ' Vantul are o miscare de la centru spre exterior sau in sensul acelor de ceasornic (pentru emisfera nordica).', ' in cazul ciclonului;', ' in cazul izotermei;', ' in cazul anticiclonului.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:38:08'),
(447, 'Meteorologie', 47, ' Forta care produce miscarea orizontala, in cazul aerului este:', ' umiditatea;', ' diferenta de presiune;', ' temperatura.', ' insolatie.', NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:38:08'),
(448, 'Meteorologie', 48, ' Norii convectivi formati prin convectie termica sau dinamica sunt:', ' Cu, Cb;', ' Ci, As, St;', ' As, Ns.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:38:08'),
(449, 'Meteorologie', 49, ' Norii se clasifica dupa inaltimea bazei fata de sol in cate categorii:', ' 3;', ' 4;', ' 5.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:42:54'),
(450, 'Meteorologie', 50, ' Norii cumulus sunt nori de:', ' inalta inaltime;', ' joasa inaltime;', ' medie inaltime.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:42:54'),
(451, 'Meteorologie', 51, ' In care nori intalnim descarcari electrice:', ' Cumulus humilis;', ' Cumulonimbus;', ' Altocumulus.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:42:54'),
(452, 'Meteorologie', 52, ' Frontul in lungul caruia aerul rece in deplasare inlocuieste aerul cald este:', ' frontul rece;', ' frontul cald;', ' frontul oclus', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:42:54'),
(453, 'Meteorologie', 53, ' Frontul cald se dezvolta atunci cand:', ' masa de aer cald impinge si inlocuieste o masa de aer rece;', ' masa de aer rece impinge si inlocuieste o masa de aer cald;', ' masa de aer rece impinge si inlocuieste o masa de aer mai rece.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:42:55'),
(454, 'Meteorologie', 54, ' Atunci cand aerul rece si cel cald din doua mase de aer invecinate se deplaseaza paralel, in acelasi sens sau chiar in sens invers, dar niciuna dintre ele nu o poate inlocui pe cealalta, avem parte de un:', ' front oclus;', ' front stationar;', ' front rece.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:42:55'),
(455, 'Meteorologie', 55, ' Ce este gradientul termic vertical ?', ' gradientul termic vertical reprezinta variatia temperaturii pentru o diferenta de nivel de  100 m.', ' miscarea orizontala a aerului.', ' diferenta de viteza a vantului pe diferite altitudini din 100 in 100 de metri.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:42:55'),
(456, 'Meteorologie', 72, '  Cand se poate afirma ca este vant laminar ?', ' cand nu sunt rafale.', ' iarna.', ' in situatia cand vantul are o miscare uniforma, atat in ce priveste viteza cat si directia,scurgerea aerului facandu-se in straturi paralele', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:42:55'),
(457, 'Meteorologie', 74, ' Din ce sunt constituiti norii ?', ' din aer cald.', ' din picaturi fine de apa sau cristale de gheata in suspensie in atmosfera.', ' din bule termice.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:42:55'),
(458, 'Meteorologie', 77, ' Este indicat sa zburam in apropierea unui Cumulonimbus (CB) ?', ' da', ' nu', ' depinde de directia vantului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:42:55'),
(459, 'Meteorologie', 78, 'Cirrus, cirrocumulus si cirrostratus sunt nori periculosi pentru parapantism ?', ' da', ' nu', ' uneori', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:42:55'),
(501, 'Performante umane', 1, 'Simptomele oboselii includ:', ' greseli mari, un slab rationament, decizii slabe sau indecizia', ' concentrare marita', ' reactii încetinite, slaba coordonare ochi-mâna', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-27 17:48:49'),
(502, 'Performante umane', 2, 'Dupa un consun exagerat de alcool,trebuie tinut cont de faptul ca alcoolul poate sa ramana in corp :', ' cel mult 8 ore', ' cel mult 24 ore', ' cel putin 30 ore', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:48:49'),
(503, 'Performante umane', 3, 'Corpul uman reactioneaza si la viteza unghiulara sau la acceleratia unghiulara atunci când va rotiti.Într-un viraj de 60 grade, acceleratia gravitationala creste si ajunge la :', ' 2G', ' 9,8G', ' 4G', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:48:49'),
(504, 'Performante umane', 4, 'Atunci când conditiile meteo nu sunt tocmai favorabile cel mai mult conteaza:', ' pregatirea teoretica a pilotului', ' nivelul tehnic al pilotului', ' atitudinea pilotului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:48:49'),
(505, 'Performante umane', 5, 'Absenta oxigenului din corp (si creier) se numeste :', ' rau de inaltime', ' lesin', ' hipoxie', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:48:49'),
(506, 'Performante umane', 6, 'Hipoxia apare datorita :', 'deshidratarii', 'lipsei oxigenului', 'stresului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:48:49'),
(507, 'Performante umane', 7, 'Inainte de zbor este obligatoriu:', ' un timp de odihna de minim 8 h', ' nu este cazul la acest tip de aeronava', ' program de pregatire fizica', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:48:49'),
(508, 'Performante umane', 8, 'Echiparea cu parasuta este obligatorie:', ' in zborurile de antrenament', ' indiferent de inaltime', ' nu este obligatorie, ramane la hotararea comandantului de aeronava', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-27 17:48:49'),
(509, 'Performante umane', 9, 'Vârsta pâna la care se poate practica parapantismul este:', ' 40 de ani', ' 45 de ani', ' nu exista limite daca starea de sanatate permite', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:48:49'),
(510, 'Performante umane', 10, 'Ce masuri sunt recomandate inaintea zborului pentru prevenirea raului de zbor ?', ' somul suficient de noapte, de minim 8 ore', ' o alimentatie adecvata', ' o echipare corespunzatoare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-27 17:48:49'),
(511, 'Performante umane', 11, 'Ce efect asupra organismului poate avea o succesiune de viraje cu inclinare mare ?', 'tulburare de vedere', 'respiratie dificila pe nas', 'pierderea cunostintei', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,3', '2015-09-27 17:51:44'),
(512, 'Performante umane', 12, 'Este considerat sub influenta bauturilor alcoolice pilotul care are o imbibatie alcoolica in sange intre :', ' 0,01 si 1', ' 0,5 si 1', ' peste 1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:51:44'),
(513, 'Performante umane', 13, 'Este considerat in stare de ebrietate pilotul care are o imbibatie alcoolicain sange :', 'mai mare de 0,5', 'mai mare de 0,9', 'mai mare de 1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:51:44'),
(514, 'Performante umane', 14, ' Este important ca un elev pilot sa fie odihnit înainte de a veni la cursuri de parapantism ?', ' sa se simta bine, asta e important', ' întotdeauna este bine ca un pilot sa fie odihnit înainte de decolare.', ' chiar daca nu s-a dormit o noapte, daca este obisnuit poate face cîteva zboruri.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:51:44'),
(515, 'Performante umane', 15, 'Care sunt cele mai importante decizii de luat cînd se decoleaza pe conditii periculoase ?', ' mentinerea aripii deasupra capului si conducerea aripi cu multa atentie.', ' deschiderea parasutei de rezerve daca avem un incident grav.', ' nu se decoleaza niciodata în conditii periculoase.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:51:44'),
(516, 'Performante umane', 16, 'Organismul uman poate suporta conditiile atmosferice întâlnite peste 5000 de m ?', ' fara echipament adecvat nu.', ' doar daca se poarta echipament cu masca de oxigen si haine extrem de groase.', ' Nu este recomandat sa se zboare cu parapante peste altitudini mai mari de 5000 m.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:51:44'),
(517, 'Performante umane', 17, 'Ce se poate întâmpla din cauza suprasarcinilor produse la manevrele de acrobatie ?', ' pilotul îsi poate pierde cunostinta.', ' fara o pregatire fizica si psihica buna nu se executa manevre de acrobatie.', ' suprasolicitarea poate duce la deteriorarea ariipi daca acesta este uzata.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,3', '2015-09-27 17:51:44'),
(518, 'Performante umane', 18, 'Instrutorul de parapantism trebuie sa fie un pilot bun ?', ' trebuie sa cunoasca pedagogia predarii dar si sa fie un pilot care sa poata demonstra cea ce preda', ' exista reglementari specific', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:51:44'),
(519, 'Performante umane', 19, 'Cum este recomandat sa se încarce parapanta în zbor ?', ' se respecta tot timpul marjele de încarcare date de fabricant.', ' în functie de conditiile meteo se poate zbura si cu aripi slab încarcate ca urca mai bine.', ' numai aripile de competitie trebuie încarcate conform standardelor.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:51:44'),
(520, 'Performante umane', 20, 'Daca avem o aripa noua de ultima generatie este nevoie de parasuta de rezerva ?', ' da  întodeauna este indicat purtarea parasutei de rezerva.', ' daca este o aripa de scoala, nu.', ' numai la zborurile tandem (în dubla comanda).', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:51:45'),
(521, 'Performante umane', 21, 'Este riscant sa folosim parasuta de siguranta ?', ' la nevoie se utilizeaza întodeauna parasuta de rezerva.', ' numai daca vrem sa aterizam întrun locul dorit', ' parasuta de rezerva este doar pentru pilotii de competitie.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-27 17:51:45'),
(522, 'Performante umane', 22, 'Este foarte cald, e bine sa ne suprasolicitam ca sa facem cit mai  multe zboruri profitînd de vremea buna ?', ' întodeauna se decoleaza pentru a profita de conditiile meteo cît mai mult.', ' numai daca se transpira din greu se obtin rezultate foarte rapid.', ' întotdeauna trebuie sa ne cunoastem limitele si sa nu ne suprasolicitam înainte de decolare.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:51:45'),
(523, 'Performante umane', 23, 'Care este vârsta legala de la care se poate practica parapantismul:', ' 21 de ani', ' sub supravegherea unui instructor se poate practica de la vârsta de 16 ani.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:51:45'),
(601, 'Cunoasterea aeronavei', 1, ' Actionarea acceleratorului are ca efect:', ' reducerea vitezei de zbor', ' dublarea vitezei de zbor', ' cresterea vitezei de zbor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-27 17:04:34'),
(602, 'Cunoasterea aeronavei', 2, ' O parapanta omologata LTF - 1 respectiv EN - A este destinata:', ' pilotilor de competitie', ' pilotilor incepatori', ' exclusiv pilotilor de acrobatie', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-27 17:04:34'),
