(709, 'Proceduri operationale', 9, ' Alegeti cea mai sigura manevra de coborare rapida:', ' urechi', ' cravata', ' vrie', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:29:57'),
(710, 'Proceduri operationale', 10, ' In timpul manevrei URECHI controlul pe directie se face:', ' exclusiv prin balansarea corpului stanga-dreapta', ' exclusv cu ajutorul comenzilor', ' exclusiv cu ajutorul chingilor Last', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:29:57'),
(711, 'Proceduri operationale', 11, ' In cazul cedarii comenzilor pilotajul parapantei se va face:', ' cu ajutorul chingii Last (chinga spate) cu amplitudine mult mai mare decat in cazul actionarii comenzilor', ' cu ajutorul chingilor B cu amplitudine mica', ' cu ajutorul chingii Last (chinga spate) cu amplitudine mult mai mica decat in cazul actionarii comenzilor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:32:32'),
(712, 'Proceduri operationale', 12, ' La ce viteza de zbor se obtine infundarea minima:', ' mai mare decat viteza la finete maxima', ' la viteza minima de zbor', ' la viteza mai mica decat viteza la finete maxima', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:32:33'),
(713, 'Proceduri operationale', 13, ' Decolarea se executa:', ' cu viteza mica pentru a nu afecta gonflajul voalurii', ' cu viteza constanta pentru a putea mentine panta de zbor', ' cu alergare accelerata progresiv', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:32:35'),
(714, 'Proceduri operationale', 14, ' Enumerati fazele decolarii:', ' gonflaj, control vizual al aripii, accelerare, desprindere', ' gonflaj, desprindere, stabilizare', ' alergare, gonflaj, desprindere, stabilizare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:32:36'),
(715, 'Proceduri operationale', 15, ' In timpul procedurii de decolare, in zbor la panta, cine are prioritate:', ' pilotul care urmeaza sa decoleze', ' pilotul care se afla in aer in zbor la panta in zona decolarii', ' daca mai exista cineva in zbor la panta decolarea este interzisa indiferent de zona de zbor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:32:36'),
(716, 'Proceduri operationale', 16, ' In timpul zborului la panta, doi piloti se afla la acelasi nivel, fata in fata. Cine are prioritate:', ' pilotul care are panta in dreapta', ' pilotul care are panta in stanga, avand prioritate de stanga', ' nu exista prioritate in zbor la panta numai in zbor termic', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:32:36'),
(717, 'Proceduri operationale', 17, ' La zborul in termica cine da sensul de spiralare:', ' primul pilot care a intrat in termica', ' ultimul pilot care a intrat in termica', ' fiecare pilot spiraleaza pe ce parte doreste, nu exista rrgula in acest sens', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:32:36'),
(718, 'Proceduri operationale', 18, ' In timpul zborului doua parapante se intalnesc la acelasi nivel, fata in fata. Care este procedura de evitare a abordajului:', ' fiecare parapanta va vira spre dreapta', ' fiecare parapanta va vira spre stanga', ' nu exista o regula in acest sens', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:32:36'),
(719, 'Proceduri operationale', 19, ' Sunteti in zbor si din dreapta dvs vine un alt aparat de zbor fara motor. Care este procedura de evitare a abordajului:', ' are prioritate aparatul care vine din dreapta, dvs veti vira la stanga sau la dreapta pt degajare', ' are prioritate aparatul care vine din stanga, adica dvs', ' are prioritate aparatul care vine din dreapta, dvs veti vira la dreapta  pt degajare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:32:36'),
(720, 'Proceduri operationale', 20, ' In timpul procedurilor de aterizare in zona sunt o parapanta si un motodeltaplan. Cine are prioritate:', ' parapanta fiind aparat de zbor fara motor', ' motodeltaplanul, avand greutate proprie mai mare', ' motodeltaplanul, avand viteza mai mare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:32:36'),
(721, 'Proceduri operationale', 21, ' Alegeti elementele obligatorii din procedura de aterizare:', ' priza in U sau L', ' vant de fata', ' degajarea zonei de aterizare cat mai rapid cu putinta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2,3', '2015-09-28 15:35:01'),
(722, 'Proceduri operationale', 22, ' Alegeti afirmati corecta:', ' decolarea se face exclusiv cu vant de spate, pt a asigura o viteza corespunzatoare in timpul alergarii', ' decolarea se face mentinand comenzile sus pana dupa desprindere', ' alergarea in timpul decolarii se face cu viteza progresiv crescatoare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:35:01'),
(723, 'Proceduri operationale', 23, ' Alegeti elementele periculoase din timpul procedurii de aterizare:', ' vant de spate', ' zbor la viteza aproape de viteza de angajare', ' priza in U sau L', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-28 15:35:01'),
(724, 'Proceduri operationale', 24, ' Alegeti elementele periculoase din timpul zborului in termica:', ' spiralarea in contrasens cu ceilalti piloti aflati in aceeasi termica', ' nesustinerea virajului', ' folosirea inclinarii corpului in timpul spiralarii', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-28 15:35:01'),
(725, 'Proceduri operationale', 25, ' Alegeti calitatile necesare unei zone de decolare:', ' sa aiba spatiu de renuntare', ' sa aiba inclinare mai mare decat finetea parapantei', ' sa aiba prag de desprindere cat mai accentuat', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-28 15:35:01'),
(726, 'Proceduri operationale', 26, ' Care este directia vantului permisa pentru decolare la panta:', ' de la zero grade (vant de fata) pana la 90 de grade lateral fata masurand de la perpendiculara pe panta', ' de la zero grade (vant de fata) pana la 45 de grade lateral fata masurand de la perpendiculara pe panta', ' este recomandata decolarea cu vant de spate atunci cand este cazul', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:35:01'),
(727, 'Proceduri operationale', 27, ' Care este viteza de desprindere recomandata in timpul procedurii de decolare:', ' viteza maxima de zbor', ' viteza la finete', ' viteza corespunzatoare vitezei minime de infundare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:35:01'),
(728, 'Proceduri operationale', 28, ' Finetea adevarata a parapantei poate varia in functie de:', ' regimul de zbor', ' traversarea unei mase de aer ascendente sau descendente', ' traversarea unei mase de aer turbulente', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2,3', '2015-09-28 15:35:01'),
(729, 'Proceduri operationale', 29, ' Tensiunea in comenzi creste datorita:', ' cresterii presiunii in voalura', ' cresterii unghiului de incidenta', ' cresterii vitezei de zbor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:35:01'),
(730, 'Proceduri operationale', 30, ' La traversarea unei zone cu descendente, pentru a pierde cat mai putina inaltime, este recomandat sa zburati la regim:', ' de infundare minima', ' la finete maxima', ' la viteza cat mai mare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:35:01'),
(731, 'Proceduri operationale', 31, ' In timpul virajului, fata de zborul pe panta normala, rectilinie,  asupra aparatului de zbor actioneaza in plus:', ' forta centrifuga', ' forta coriolis', ' efectul coanda', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:37:32'),
(732, 'Proceduri operationale', 32, ' Pilotajul activ inseamna:', ' mentinerea unei tensiuni cat mai constante in comenzi, si folosirea balansarii laterale pentru echilibrare, in timpul zborului in turbulente', ' folosirea balansarii laterale pentru echilibrare in timpul zborului in turbulente', ' folosirea exclusiva a comenzilor in timpul zborului in turbulente', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:37:32'),
(733, 'Proceduri operationale', 33, ' Alegeti afirmatia corecta:', ' la traversarea unei zone turbulente finetea scade', ' la traversarea unei zone descendente finetea creste', ' la traversarea unei zone descendente finetea ramane neschimbata', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:37:32'),
(734, 'Proceduri operationale', 34, ' O parapanta omologata LTF 2 (EN -C) este destinata:', ' exclusiv pilotilor incepatori', ' exclusiv zborului in tandem', ' exclusiv pilotilor cu nivel mediu/ridicat de pregatire', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:37:32'),
(735, 'Proceduri operationale', 35, ' Zborul cu pasager presupune:', ' licenta de pilot', ' licenta de pilot calificarea tandem', ' parapanta omologata LTF 1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:37:32'),
(736, 'Proceduri operationale', 36, ' In timpul manvrei URECHI este permis:', ' sa folositi balansarea laterala pentru controlul pe directie', ' sa actionati speedul', ' sa executati si B stall pentru o coborare mai eficienta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-28 15:37:32'),
(737, 'Proceduri operationale', 37, ' Controlul traiectoriei in timpul spiralarii se face:', ' exclusiv folosind comenzile', ' exclusiv folosind balansarea laterala a corpului comenzile fiind mentinute la 50% din cursa pentru a obtine o infundare minima', ' folosind balansarea laterala a corpului si comenzile', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:37:32'),
(738, 'Proceduri operationale', 38, ' In cazul inchiderii asimetrice, procedura de recuperare este:', ' balansarea laterala a corpului, incarcand aripa deschisa, corijarea moderata a directiei folosind comanda de pe aripa deschisa si pomparea aripii inchise', ' balansarea laterala a corpului si eliberarea completa a ambelor comenzi', ' pomparea energica a aripii inchise folosind comanda, si eliberarea compleata a comenzii de pe aripa deschisa', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:37:32'),
(739, 'Proceduri operationale', 39, ' Ce fenomene se regasesc in timpul inchiderii asimetrice:', ' cresterea rezistentei la inaintare a aripii inchise', ' lipsa de comanda corespunzatoare aripii inchise', ' tendinta de viraj a parapantei pe partea aripii inchise', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,3', '2015-09-28 15:37:32'),
(740, 'Proceduri operationale', 40, ' In timpul virajului, la atingerea unghiului critic, parapanta va intra in :', ' deepstall', ' vrie (negativa)', ' spirala picata', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:37:32'),
(741, 'Proceduri operationale', 41, ' In urma unei inchideri asimetrice majore, peste 50%, observati ca suspantele sunt rasucite (twist). Care este manevra corecta:', ' lansarea imediata a rezervei', ' inclinarea corpului pe exteriorul spiralei si dezrasucirea imediata', ' dezrasucirea folosind chingile portsuspante fara a elibera comenzile', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:39:58'),
(742, 'Proceduri operationale', 42, ' Alegeti motivele pentru care trebuie sa renuntati la decolare chiar daca aveti aripa gonflata corect:', ' casca nelegata', ' vant de spate', ' turbulente usoare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-28 15:39:58'),
(743, 'Proceduri operationale', 43, ' Alegeti motivele pentru care trebuie sa renuntati la decolare chiar daca aveti aripa gonflata corect:', ' suspante incalcite', ' declansare termica', ' alta parapanta in procedura de toplanding', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,3', '2015-09-28 15:39:58'),
(744, 'Proceduri operationale', 44, ' In timpul procedurii de aterizare viteza recomandata pentru apropiere este:', ' aproape de viteza minima de zbor', ' aproape de viteza maxima de zbor', ' intre viteza corespunzatoare infundarii minime si finetei maxime', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:39:58'),
(745, 'Proceduri operationale', 45, ' In timpul zborului la finete maxima sau la viteza maxima care este principalul pericol daca intalnim turbulente:', ' inchidere frontala sau asimetrica datorita unghiului de incidenta mic', ' angajare in limita de viteza datorita unghiului de incidenta mare', ' vrie datorita regimului de zbor asimetric', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:39:58'),
(746, 'Proceduri operationale', 46, '  Zburati intr-o zona muntoasa, pietroasa, cu vai (duze). Ce situatii dificile credeti ca veti intalni:', ' termica violenta', ' rotoare in zonele de sub vant', ' efect venturi la traversarea vailor sau pe creste', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2,3', '2015-09-28 15:39:58'),
(747, 'Proceduri operationale', 47, ' Care dintre urmatoarele sunt manevre de coborare rapida:', ' vrie', ' spirala picata', ' urechi', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2,3', '2015-09-28 15:39:58'),
(748, 'Proceduri operationale', 48, ' Inainte de decolare este obligatoriu:', ' sa verificam directia si viteza vantului in locul de decolare', ' sa verificam directia si viteza vantului in minim 3 locuri pe panta de zbor', ' sa verificam directia si viteza vantului in zona de decolare si de aterizare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:39:59'),
(749, 'Proceduri operationale', 49, ' In zonele rotorice (sub vant, in spatele pantei) exista posibilitatea ca o maneca de vant sa arate vant de fata:', ' da, daca este in zona ascendenta a rotorului', ' nu, maneca de vant va arata intodeauna vant de spate', ' nu, in rotor curgerea masei de aer este haotica dar niciodata ascendenta pe panta de sub vant.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:39:59'),
(750, 'Proceduri operationale', 50, ' Contactul cu solul la aterizare se va face:', ' la unghi critic', ' la viteza manima de infundare', ' la finete maxima', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:39:59'),
(751, 'Proceduri operationale', 51, ' Alegeti care dintre urmatoarele fenomene pot fi intalnite in zbor la panta:', ' efect venturi', ' efect coriollis', ' efect de camp', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:42:27'),
(752, 'Proceduri operationale', 52, ' In procedura de decolare este necesar:', ' sa decolati cu comenzile la inele, corespunzator vitezei la finete maxima', ' sa mentineti viteza de zbor constanta pe tot timpul decolarii pana dupa desprindere', ' sa alergati accelerand progresiv pana la desprindere', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:42:27'),
(753, 'Proceduri operationale', 53, ' La intrarea in termica sunteti obligat sa:', ' acordati prioritate celor ce spiraleaza deja in termica', ' folositi acceleratorul imediat ce creste vectorul urcarii pentru a obtine o urcare constanta', ' folositi doar patrularea in 8 - uri pentru a avea urcare constanta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:42:27'),
(754, 'Proceduri operationale', 54, ' In zbor la panta, de la ce inaltime puteti trece la spiralare:', ' minim 50m', ' imediat ce ati intrat in termica', ' minim 150m', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:42:27'),
(755, 'Proceduri operationale', 55, ' Care dintre urmatoarele categorii de omologare nu permit zborul in termica:', ' EN - A', ' EN - B', ' toate parapantele omologate pot sa zboare in termica', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:42:27'),
(756, 'Proceduri operationale', 56, ' La intrarea in termica, la acelasi nivel cu dvs mai spiraleaza un deltaplan. Cum veti proceda:', ' intrati respectand sensul de spiralare al deltaplanului, in spatele lui', ' incepeti spiralarea in orice sens, parapanta are intodeauna prioritate', ' intrati in termica in fata deltaplanului pentru a fi vizibil si spiralati impreuna parapanta avand intodeauna prioritate inclusiv la schimbarea sensului de spiralare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:42:27');
INSERT INTO `questions` (`id`, `category`, `category_index`, `text`, `answer_1`, `answer_2`, `answer_3`, `answer_4`, `answer_5`, `answer_6`, `answer_7`, `answer_8`, `answer_9`, `answer_10`, `correct`, `date`) VALUES
(757, 'Proceduri operationale', 57, ' In zbor la panta, aveti panta in partea stanga, din fata la acelasi nivel vine un deltaplan. Cum veti proceda:', ' mentineti directia, parapanta are totdeauna prioritate', ' degajati spre dreapta si acordati prioritate deltaplanului', ' degajati spre stanga si acordati prioritate deltaplanului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:42:27'),
(758, 'Proceduri operationale', 58, ' Care este ordinea corecta de echipare pentru zbor:', ' casca, seleta, parapanta', ' seleta, parapanta, casca', ' parapanta, seleta casca', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:42:27'),
(759, 'Proceduri operationale', 59, ' Ce trebuie sa faceti la traversarea unei zone ascendente, care duce la marirea inaltimii de zbor cu 5-15m, in timpul procedurii de aterizare:', ' franati puternic parapanta mentinad directia neschimbata', ' printr-o serie de viraje in S veti reveni la panta normala de aterizare', ' actionati speedul pentru a intra pe panta normala de aterizare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:42:27'),
(760, 'Proceduri operationale', 60, ' Imediat dupa decolare comanda stanga se dezleaga de la nod, devenind inoperabila. Cum veti proceda:', ' veti pilota folosind exclusiv comanda dreapta si balansarea corpului in lateral, mergand cat mai repede la aterizare', ' veti pilota folosind balansarea corpului in lateral si chingile Last, cu amplitudine mai mica, mergand cat mai repede la aterizare', ' veti pilota exclusiv folosind balasarea laterala a corpului, mergand cat mai repede la aterizare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:42:27'),
(761, 'Proceduri operationale', 61, ' Care sunt riscurile principale la traversarea unei zone turbulente la regim de finete maxima sau viteza maxima:', ' inchideri majore eventual twist', ' angajare si full stall', ' nu sunt riscuri suplimentare in aceste regimuri de zbor, toate parapantele omologate fiind stabile', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:45:00'),
(762, 'Proceduri operationale', 62, ' Alegeti afirmatia corecta:', ' la intrarea in termica peste 3 m/s veti frana brusc pentru a zbura la regim de infundare minima cat mai repede', ' la intrarea in termica de peste 3 m/s veti mentine aripa stabila prin pilotaj activ si apoi incepeti centrarea si zborul la regim de infundare minima', ' la intrarea in termica de peste 3m/s incepeti  urgent spiralarea pentru a ajunge la miez cat mai repede', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:45:01'),
(763, 'Proceduri operationale', 63, ' In timpul executarii manvrei B stall viteza pe orizontala a parapantei se va modifica astfel:', ' va creste', ' va scadea pana aproape de zero', ' va ramane neschimbata', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:45:01'),
(764, 'Proceduri operationale', 64, '  Alegeti manevrele de coborare rapida:', ' vrie', ' fulstall', ' B-stall', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:45:01'),
(765, 'Proceduri operationale', 65, ' Alegeti afirmatia corecta:', ' cu manevra urechi se poate regla panta inainte de aterizare', ' cu manevra urechi nu se poate regla panta inainte de aterizare', ' cu manevra b- stall se poate regla panta inainte de aterizare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:45:01'),
(766, 'Proceduri operationale', 66, ' La aterizarea in zone care prezinta ascendenta usoara sau turbulenta usoara se recomanda:', ' aterizarea cu urechi', ' aterizarea cu b-stall', ' aterizarea cu front-stall', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:45:01'),
(767, 'Proceduri operationale', 67, ' La stabilirea traiectului de zbor este obligatoriu sa luati in considerare:', ' spatiul de renuntare', ' traseu alternativ', ' traiectul sondei meteo', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:45:01'),
(768, 'Proceduri operationale', 68, ' Spatiul de renuntare este:', ' zona de aterizare alternativa', ' traiect alternativ', ' spatiul cuprins in zona de decolare care permite renuntarea decolarii in conditii de securitate', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:45:01'),
(769, 'Proceduri operationale', 69, ' Alegeti afirmatia corecta:', ' in timpul virajului viteza de infundare scade', ' in timpul virajului viteza de infundare creste', ' in timpul virajului viteza de infundare ramane constanta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:45:01'),
(770, 'Proceduri operationale', 70, ' Alegeti conditiile obligatorii necesare zborului cu parapanta in Romania:', ' informare la COAP', ' lipsa totala a altor categorii de aeronave in zona de zbor', ' conditii meteo CAVOK', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:45:01'),
(771, 'Proceduri operationale', 71, ' Mentiunea CAVOK in METAR presupune in mod obligatoriu:', ' interzicerea zborului, conditii meteo periculoase', ' interzicerea zborului, trafic aerian aglomerat in zona', ' nu exista restrictii speciale in acest caz', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:46:41'),
(772, 'Proceduri operationale', 72, ' Sunteti in termica impreuna cu alte parapante si in aceeasi termica intra si un planor. Cum veti proceda:', ' parasiti termica, planorul fiind clasa superioara, are prioritate', ' continuati sa zburati mentinand sensul de spiralare', ' continuati sa zburati si schimbati sensul de spiralare pentru a fi mai vizibil de catre planor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:46:41'),
(773, 'Proceduri operationale', 73, ' In zbor la panta, are prioritate neconditionata:', ' parapanta care vine din dreapta', ' parapanta care decoleaza', ' parapanta in procedura de aterizare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:46:41'),
(774, 'Proceduri operationale', 74, '  Alegeti procedura sigura in cazul aterizarii pe teren necunoscut:', ' apropierea in priza directa', ' apropierea cu priza in L sau U', ' apropierea cu urechi sau B-stall', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:46:41'),
(775, 'Proceduri operationale', 75, ' In timpul virajului stabilizat si sustinut viteza de curgere a fileurilor de aer pe cele doua semiaripi este:', ' mai mare pe aripa exterioara', ' mai mica pe aripa exterioara', ' egala', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:46:41'),
(776, 'Proceduri operationale', 76, ' La traversarea unei zone turbulente se recomanda:', ' aterizarea', ' actionarea speedului', ' eliberarea speedului daca acesta este actionat', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:46:41'),
(777, 'Proceduri operationale', 77, ' Alegeti manevrele de coborare rapida:', ' spirala picata', ' B-stall', ' vrie', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-28 15:46:41'),
(778, 'Proceduri operationale', 78, ' La ce se foloseste actionarea suspantei stabilo :', ' la executarea manevrei de urechi', ' la pilotaj atunci cand comenzile devin inoperabile', ' la scoaterea unei cravate', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:46:41'),
(779, 'Proceduri operationale', 79, ' Cand se recomanda actionarea suspantelor stabilo concomitent si simetric:', ' niciodata', ' la executarea manevrei de urechi', ' la exedutarea manevrei B-stall', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:46:41'),
(780, 'Proceduri operationale', 80, ' Alegeti afirmatia corecta:', ' decolarea cu vant zero este interzisa', ' decolarea cu vant zero presupune viteza de alergare mai mare fata de decolarea pe conditii de vant de fata', ' decolarea cu vant zero presupune viteza de alergare mai mare fata de decolarea pe conditii de vant de spate', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:46:41'),
(781, 'Proceduri operationale', 81, ' In timpul aterizarii pe panta se recomanda:', ' aterizarea in contrapanta', ' aterizarea in lungul pantei', ' aterizarea folosind acceleratorului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:49:19'),
(782, 'Proceduri operationale', 82, '  Actionarea acceleratorului are ca efect:', ' reducerea vitezei de zbor', ' dublarea vitezei de zbor', ' cresterea vitezei de zbor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:49:19'),
(783, 'Proceduri operationale', 83, ' Efectul venturi poate fi intalnit:', ' pe creste cand vantul este perpendicular pe creasta', ' pe vai cand vantul este in lungul vaii', ' nu poate fi regasit in natura fiind doar un fenomen aerodinamic', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-28 15:49:19'),
(784, 'Proceduri operationale', 84, ' Reglajul vitezei de zbor se face:', ' folosind comenzile sau acceleratorul', ' folosind exclusiv trimerul', ' folosind exclusiv acceleratorul', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:49:19'),
(785, 'Proceduri operationale', 85, ' Fazele aterizarii sunt:', ' priza, redresare, franare finala', ' priza, franare finala', ' redresare, fullstall', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:49:19'),
(786, 'Proceduri operationale', 86, ' Care dintre urmatoarele reprezinta pericol major in procedura de aterizare:', ' angajarea la mai mult de 0,5m inaltime', ' inchideri asimetrice la mai putin de 200m inaltime', ' angajarea la mai putin de 0,5m inaltime', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:49:19'),
(787, 'Proceduri operationale', 87, ' Regimul de zbor la finete se foloseste in urmatoarele cazuri:', ' dupa centrarea in termica si trecerea la spiralare', ' pentru centrarea eficienta in termica', ' pentru obtinerea unghiului maxim de panta, de exemplu la salt (tranzitie) de la o termica la alta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:49:19'),
(788, 'Proceduri operationale', 88, ' In timpul manevrei urechi:', ' creste unghiul de incidenta', ' scade unghiul de incidenta', ' unghiul de incidenta ramane neschimbat', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:49:19'),
(789, 'Proceduri operationale', 89, ' In timpul procedurii de decolare unghiul de atac este:', ' variabil', ' fix', ' variaza doar daca se actioneaza acceleratorul', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:49:19'),
(790, 'Proceduri operationale', 90, ' Regimurile de zbor ale parapantei sunt:', ' viteza minima, viteza maxima, viteza accelerata', ' viteza la infundare minima, viteza la finete maxima, viteza maxima', ' viteza de angajare, viteza maxima, viteza la finete', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:49:20'),
(791, 'Proceduri operationale', 91, ' Cresterea incarcarii alare modifica performantele parapantei:', ' da', ' nu', ' incarcarea alara nu are legatura cu domeniul zborului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:49:20'),
(792, 'Proceduri operationale', 92, ' In momentul angajarii unei parapante', ' presiunea in comenzi scade brusc', ' viteza de inaintare creste brusc', ' viteza de infundare creste brusc', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,3', '2015-09-28 15:49:20'),
(793, 'Proceduri operationale', 93, ' Care este diferenta dintre vrie si spirala picata:', ' in cazul vriei fileurile de aer se desprind de pe extrados', ' in cazul spiralei picate fileurile de aer se desprind de pe extrados', ' ambele manevre sunt identice din punct de vedere aerodinamic, este doar o diferenta de terminologie', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:49:20'),
(794, 'Proceduri operationale', 94, ' La ce viteza de zbor se obtine infundarea minima:', ' mai mare decat viteza la finete maxima', ' la viteza minima de zbor', ' la viteza mai mica decat viteza la finete maxima', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:49:20'),
(795, 'Proceduri operationale', 95, ' Rezerva se arunca de preferat in cazul unei rotatii necontrolabile:', ' in directia rotatiei', ' contra rotatiei', ' nu are importanta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 15:49:20'),
(796, 'Proceduri operationale', 96, ' La aruncarea rezervei se da drumul la maner:', ' da', ' nu', ' nu conteaza', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:49:20'),
(797, 'Proceduri operationale', 97, ' In cazul ruperii unei comenzi franarea la aterizare  se face prin:', ' tragerea simetrica a chingilor LAST', ' tragerea simetrica a chingilor C', ' tragerea simetrica a suspantelor  stabilo', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 15:49:20'),
(798, 'Proceduri operationale', 98, ' Mentinerea pozitiei aplecat in timpul alergarii pentru decolare faciliteaza:', ' manevrabilitatea parapantei', ' reglajul vitezei', ' mentinerea tensiunii constante a suspantelor pe parcursul procedurii de decolare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 15:49:20'),
(801, 'Prim ajutor', 1, ' Cu ce se recomanda transportarea persoanelor accidentate, care prezinta fracturi de coloana?', ' cu un autoturism, deoarece asigura ranitului confort', ' cu orice mijloc de transport', ' cu un mijloc de transport cu platforma', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:03:00'),
(802, 'Prim ajutor', 2, ' Care este prima masura pe care trebuie sa o luati cand o persoana, ce a fost implicata intr-un accident, are o rana cu hemoragie?', ' sa pansati rana', ' sa opriti hemoragia', ' sa curatati rana', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:03:00'),
(803, 'Prim ajutor', 3, ' Cum se face oprirea hemoragiei ?', ' prin legarea stransa a bratului deasupra rani', ' prin legarea lejera a bratului deasupra ranii', ' prin legarea stransa a bratului in dreptul ranii', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:03:00'),
(804, 'Prim ajutor', 4, ' Ce trebuie sa faceti, in primul rand, pentru a salva o persoana implicata intr-un accident?', ' sa-i eliberati si sa-i degajati caile respiratorii', ' sa-i indreptati membrele fracturate', ' sa-i curatati ranile', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:03:00'),
(805, 'Prim ajutor', 5, ' Cum trebuie asezat un ranit care prezinta leziuni ale coloanei vertebrale?', ' in aceasta situatie este recomandabil sa nu fie miscat pana la sosirea ambulantei', ' in pozitie sezanda', ' orizontal, cu fata in sus', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:03:00'),
(806, 'Prim ajutor', 6, ' Ce trebuie sa aveti in vedere inaintea transportarii unei victime?', ' ca autovehiculul cu care o veti transporta sa fie confortabil', ' ca functiile respiratorii si circulatorii sa fie asigurate', ' ca victima sa fie asistata, in timpul transportului, de o persoana competenta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:03:00'),
(807, 'Prim ajutor', 7, ' Pentru ce se folosesc substantele antiseptice?', ' pentru oprirea hemoragiei', ' pentru dezinfectia plagilor', ' pentru calmarea durerilor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:03:00'),
(808, 'Prim ajutor', 8, ' Ce se aplica pe plaga, intr-un pansament?', ' o compresa oarecare', ' o compresa de vata sterila', ' o compresa de tifon steril', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:03:00'),
(809, 'Prim ajutor', 9, ' Cum trebuie asezata o victima care a pierdut mult sange?', ' in pozitie sezanda', ' culcata pe o parte', ' cu picioarele situate mai sus decat nivelul corpului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:03:00'),
(810, 'Prim ajutor', 10, ' Ce se impune pentru tratamentul de urgenta al unor plagi?', ' curatarea, dezinfectia si pansarea plagilor', ' punerea tijelor de lemn pe zona ranita', ' pansarea plagilor', NULL, NULL, NULL, NULL, NULL, NULL, '', '1', '2015-09-28 16:27:20'),
(811, 'Prim ajutor', 11, ' Ce trebuie sa faceti pentru a evita complicatiile in cazul unei fracturi?', ' sa curatati si sa dezinfectati zona', ' sa pansati zona fracturata', ' sa imobilizati zona fracturata', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:05:02'),
(812, 'Prim ajutor', 12, ' Cum se apreciaza gravitatea unei hemoragii?', ' in functie de culoarea sangelui pierdut', ' in functie de fluiditatea sangelui pierdut', ' in functie de cantitatea sangelui pierdut', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:05:02'),
(813, 'Prim ajutor', 13, ' Cand se transporta persoanele accidentate ce prezinta hemoragii externe?', ' imediat dupa oprirea hemoragiei', ' imediat dupa ce o persoana calificata i-a acordat asistenta medicala', ' imediat dupa sosirea unui mijloc de transport dotat cu platforma', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:05:02'),
(814, 'Prim ajutor', 14, ' Cum se pot opri hemoragiile la trunchi si la cap?', ' prin aplicarea compreselor sterile si a pansamentului compresiv', ' prin aplicarea garoului', ' prin aplicarea atelelor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:05:02'),
(815, 'Prim ajutor', 15, ' Cu ce se dezinfecteaza plagile inainte de a fi pansate?', ' acid sulfuric', ' cu apa oxigenata', ' cu iod', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:05:02'),
(816, 'Prim ajutor', 16, ' Unde se aplica garoul in cazul plagilor insotite de hemoragii arteriale?', ' peste plaga', ' deasupra plagii', ' sub plaga', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:05:02'),
(817, 'Prim ajutor', 17, ' Cum trebuie asezata o persoana ranita care prezinta leziuni ale coloanei vertebrale?', ' orizontal, cu fata in sus', ' in pozitie sezanda', ' in situatia aceasta nu trebuie sa fie miscat pana la sosirea salvarii', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:05:02'),
(818, 'Prim ajutor', 18, ' Ce se scrie pe biletul care se prinde langa garoul aplicat unei persoane ranite?', ' ora si minutul aplicarii garoului', ' datele de identificare ale victimei', ' pozitia in care a fost gasita victima', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:05:02'),
(819, 'Prim ajutor', 19, ' Cum trebuie sa stea o persoana, ce a suferit leziuni intr-un accident, pana la transportarea la spital?', ' intr-o pozitie cat mai comoda', ' obligatoriu cu fata in sus', ' intr-o pozitie care sa permita o buna respiratie', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:05:02'),
(820, 'Prim ajutor', 20, ' Cum se poate face respiratia artificiala in cazul in care gura ranitului a ramas inclestata?', ' prin narile victimei', ' in cazul acesta nu se face respiratie artificiala', ' in acest caz se face o incizie in obrazul victimei, pe unde apoi se face respiratia artificiala', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:05:02'),
(821, 'Prim ajutor', 21, ' Cum trebuie sa fie atelele pentru imobilizarea unei fracturi?', ' sa nu fie foarte grele', ' foarte flexibile', ' sa fie suficient de lungi pentru a acoperi in intregime atat zona de deasupra, cat si cea de sub fractura', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,3', '2015-09-28 16:06:41'),
(822, 'Prim ajutor', 22, ' De ce se aplica un bandaj de tifon pe o rana deschisa?', ' pentru a opri hemoragia', ' pentru calmarea durerilor', ' pentru a impiedica infectarea ranii', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2,3', '2015-09-28 16:06:41'),
(823, 'Prim ajutor', 23, ' Ce simptome prezinta o victima a unui accident care a suferit stop cardiac?', ' aparitia hemoragiei pe gura', ' lipsa pulsului', ' innegrirea fetei si inrosirea ochilor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:06:41'),
(824, 'Prim ajutor', 24, ' Care poate fi cauza stopului respirator in cazul persoanelor ce au fost implicate intr-un accident?', ' pierderea unei cantitati mari de sange', ' o leziune puternica in zona lombara', ' blocarea cailor respiratorii', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:06:41'),
(825, 'Prim ajutor', 25, ' Cum sa executati masajul cardiac unei persoane care a fost implicata intr-un accident , in urma caruia i s-au oprit bataile inimii?', ' prin lovirea usoara cu palmele a obrajilor victimei', ' prin apasarea ritmica cu podul palmelor suprapuse a toracelui victimei, in dreptul inimii', ' numai o persoana calificata trebuie sa faca masajul cardiac', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:06:41'),
(826, 'Prim ajutor', 26, ' Cum se face imobilizarea membrului inferior ce a fost fracturat intr-un accident?', ' cu ajutorul atelelor', ' legand strans garoul in jurul membrului', ' prin bandajarea locului fracturii', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:06:41'),
(827, 'Prim ajutor', 27, ' Cat timp poate fi mentinut aplicat un garou unei persoane ranite in urma unui accident, fara a-i leza tesuturile?', ' cel mult 12 ore', ' cel mult 2 ore', ' cel putin 2 ore', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:06:41'),
(828, 'Prim ajutor', 28, ' Cum va fi ridicata o persoana grav ranita intr-un accident?', ' cu atentie, de maini si de picioare de catre doua persoane', ' cu atentie, mentinandu-se in acelasi plan capul, gatul si toracele', ' mentinand picioarele in sus si capul ceva mai jos decat trunchiul, pentru o mai buna circulatie a sangelui in organism', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:06:41'),
(829, 'Prim ajutor', 29, ' Cum se va opri o hemoragie produsa la unul din membrele unei persoane ranite?', ' prin aplicarea garoului', ' prin actiunea compresiva asupra vasului lezat', ' prin apasarea ritmica a plagii cu podul palmelor suprapuse', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-28 16:06:41'),
(830, 'Prim ajutor', 30, ' Caile respiratorii se pot astupa din cauza ca:', ' victima a suferit un atac de panica', ' victimei i-au intrat corpuri straine pe caile respiratorii', '  victima sufera de scolioza', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:06:41'),
(831, 'Prim ajutor', 31, ' Ce este o entorsa?', ' leziune inchisa a unei articulatii, care a fost fortata sa faca o miscare / deplasare exagerata, dupa care suprafetele articulare ale oaselor si-au revenit la pozitia normala', ' leziune a unei articulatii, la care suprafetele articulare ale oaselor sunt deplasate permanent din pozitia normala', ' intreruperea totala sau partiala a continuitatii unui os', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:07:34'),
(832, 'Prim ajutor', 32, ' Ce este  luxatia?', ' leziune inchisa a unei articulatii, care a fost fortata sa faca o miscare / deplasare exagerata, dupa care suprafetele articulare ale oaselor si-au revenit la pozitia normala', ' leziune a unei articulatii, la care suprafetele articulare ale oaselor sunt deplasate permanent din pozitia normala', ' intreruperea totala sau partiala a continuitatii unui os', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:07:34'),
(901, 'Legislatie', 1, '  Care este principalul act normativ care reglementeza activitatea de zbor in Romania?', ' OMTCT 630/2007;', ' Legea 399/2005;', ' HG 912/2010;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:10:04'),
(902, 'Legislatie', 2, ' RACR-LPAN AUN stabileste cerintele specifice aplicabile pentru:', ' obtinerea revalidarea, si reinoirea licentei de pilot aeronave ultrausoare', ' autorizarea ca examinatori a persoanelor care organizeaza si efectueaza examinarile teoretice si practice', ' obtinerea altor calificari specifice licentei de pilot aeronave ultrausoare;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2,3', '2015-09-28 16:10:04'),
(903, 'Legislatie', 3, ' Principala autoritate din Romania in domeniul aeronautic este:', ' AACR- Autoritatea Aeronautica Civila;', ' MTI - Ministerul Transporturilor si Infrastructurii;', ' AZLR - Asociatia de Zbor Liber din Romania;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:10:04'),
(904, 'Legislatie', 4, ' Zborurile in interes propriu reprezinta conform Codului Aerian:', ' operatiuni de  aviatie generala, efectuate de persoane fizice sau juridice, cu aeronave civile proprii sau inchiriate, pentru si in sustinerea nevoilor proprii, fara perceperea de tarife', ' operatiuni de  aviatie generala, efectuate de catre detinatorii de aeronave civile, persoane fizice , exclusiv in scop necomercial', ' operatiuni de  aviatie generala organizate de persoane fizice sau juridice cu aeronave civile proprii in vederea pregatirii personalului aeronautic civil navigant, fara perceperea de tarife', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:10:04'),
(905, 'Legislatie', 5, ' Pentru obtinerea  licentei de instructor AUN, o persoana trebuie sa indeplineasca urmatoarele conditii specifice:', ' sa demonstreze ca a abolvit in ultimele 12 luni un program de pregatire teoretica si practica la sol si in zbor care cuprinde disciplina " Metodologia zborului in tandem" cu un instructor autorizat;', ' sa detina  de cel putin 5 ani o licenta de pilot de aeronave ultrausoare nemotorizate din clasa pentru care solicita calificarea, in termen de valabilitate', ' sa aiba varsta de 21 ani impliniti, sa detina un certificat medical corespunzator.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:10:04'),
(906, 'Legislatie', 6, ' Documentele necesare reinoirii licentei de pilot sunt urmatoarele:', ' certificat medical corespunzator,  carnet de zbor care sa ateste activitatea de zbor declaratie de responsabilitate, teste teoretice, proces verbal de examinare, cerere specifica.', ' certificat medical corespunzator, teste teoretice, proces verbal de examinare, carnet de zbor, cerere de specifica;', ' certificat medical corespunzator, fisa de pregatire teoretica si practica, teste de  examinare, proces verbal de examinare, carnet de  zbor, cerere specifica.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:10:04'),
(907, 'Legislatie', 7, ' Varsta minima de la care o persoana romana poate desfasura activitati  de zbor cu aeronave ultrausoare nemotorizate in calitate de pilot este conform OM630/2007:', '   16 ani impliniti;', ' 18 ani impliniti;', ' 15 ani impliniti;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:10:04'),
(908, 'Legislatie', 8, '   Eliberarea licentelor personalului aeronautic navigant , aeronave ultrausoare nemotorizate se face conform legii:', ' postal , detinatorului licentei;', ' doar titularului pe baza de semnatura sau unui imputernicit legal al acestuia', ' instructorului care a efectuat pregatirea sau a depus dosarul titularului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:10:04'),
(909, 'Legislatie', 9, ' Parapantele de tandem pentru a putea efectua zboruri cu alte persoane decat elevii piloti  e obligatoriu sa detina:', ' certificat de identificare, sau certificat de omologare', ' stampila sau eticheta fabricantului sau orice alt document de omologare', ' nu este necesara nici un document', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:10:04'),
(910, 'Legislatie', 10, ' Conform RACR-REAC, in categoria activitatilor ulterioare raportarii evenimentelor de aviatie civila obligatia agentului aeronautic civil este:', ' sa desfasoare o cercetare/investigatie proprie interna interna imediata pentru orice eveniment care a afectat sau poate afecta siguranta in vederea stabilirii de masuri corective, necesare pentru prevenirea unor elemente similare', ' sa verifice daca masurile corective necesare  stabilite in urma cercetarii/investigatiei sunt implementate si efective', ' sa investigheze tehnic accidentele si incidentele grave conform legii.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:10:04'),
(911, 'Legislatie', 11, ' Orice accident sau incident grav de aviatie civila se raporteaza telefonic imediat sau in scris in maxim 6 ore direct la :', ' AACR, IavC;', ' AACR, AZLR;', ' AACR, AR.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:13:10'),
(912, 'Legislatie', 12, ' In conformitate cu OM630/2007, o aeronava AUN neomologata, poate fi folosita in procesul de instruire?', ' pentru parapante si deltaplane nu este necesar ca aeronava sa fie omologata, nicaieri, in consecinta poate fi folosita in procesul de instruire;', ' poate fi folosita in procesul de instruire doar daca proprietarul acesteia este si persoana instruita;', ' in procesul de instruire se folosesc doar aeronave omologate care poseda certificat de identificare, sau certificat de omologare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:13:10'),
(913, 'Legislatie', 13, ' Conform legislatiei aeronautice in vigoare cu privire la domeniul aeronavelor ultrausoare nemotorizate, continutul minim obligatoriu al programei de pregatire teoretica si practica la sol si in zbor a pilotilor din clasa parapanta este:', ' 50 ore teorie si 12 ore de zbor', ' 50 ore teorie si 10 ore de zbor', ' 50 ore teorie si 15 ore de zbor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:13:10'),
(914, 'Legislatie', 14, ' Varsta minima de la care o persoana romana poate desfasura activitati  de zbor cu aeronave ultrausoare nemotorizate este conform OM630/2007:', ' 16 ani impliniti;', ' 18 ani impliniti;', '   15 ani impliniti', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:13:10'),
(915, 'Legislatie', 15, ' RACR-REAC, se aplica de catre persoanele fizice si juridice  implicate in activitati aeronautice ori de cate ori:', ' un accident sau incident grav s-a produs pe teritoriul Romaniei indiferent de inmatriculare sau operator;', ' un incident s-a produs in afara teritoriului Romaniei si este in legatura cu o aeronava civila, inmatriculata in Romania, sau operata de un agent aeronautic civil;', ' aceasta reglemetare se aplica doar de catre personalul civil aeronautic navigant;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-28 16:13:10'),
(916, 'Legislatie', 16, '  Procesul verbal de examinare se intocmeste de catre:', ' elevul pilot;', ' examinatorul autorizat;', ' instructorul care a efectuat pregatirea.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:13:10'),
(917, 'Legislatie', 17, '  Valabilitatea testelor teoretice si a testelor de indemanare  si competenta in zbor este:', ' 3 luni de la data sustinerii acestora;', ' 6 luni de la data sustinerii acestora;', ' 12 luni de la data sustinerii acestora.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:13:10'),
(918, 'Legislatie', 18, '  Revalidarea este:', ' procedeu administrativ efectuat in interiorul perioadei de valabilitate a unei calificari sau autorizatii;', '  procedeu administrativ efectuat dupa ce ce o calificare sau autorizatie a expirat;', '  nici unul din raspunsurile enumerate mai sus.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:13:10'),
(919, 'Legislatie', 19, '    Securitatea zborului este:', ' capacitatea sistemului aeronautic de a indeplini in mod satisfacator functiunile sale la un moment dat sau intr-o perioada de timp data cand e folosit in anumite conditii;', ' capacitatea activitatii aeronautice constand in evitarea afectarii sanatatii sau pierderii de vieti omenesti, precum si a producerii de pagube materiale;', ' un eveniment legat de utilizarea unei aeronave ca urmare a aparitiei intamplatoare a unor factori de natura sa puna in pericol activitatea de zbor.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:13:10'),
(920, 'Legislatie', 20, '       Pentru a opera in conditiile legii o aeronava AUN, o persoana este necesar sa detina:', ' licenta de pilot pe clasa de aeronave pe care opereaza;', ' asigurare pentru pagube produse tertilor;', ' informare de zbor sau dupa caz plan de zbor.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2,3', '2015-09-28 16:13:10'),
(921, 'Legislatie', 21, '       Timpul minim  de pregatire recomandat la disciplina proceduri operationale este:', ' 4 ore;', ' 2 ore;', ' 6 ore', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:17:19'),
(922, 'Legislatie', 22, '        Pentru revalidarea licentei de pilot aeronave ultrausoare nemotorizate este necesar ca si conditie specifica ca solicitantul sa  demonstreze ca  :', ' a efectuat in ultimele 12 luni minimum 5 ore de zbor incluzand 10 decolari si 10 aterizari in calitate de pilot pe clasade aeronave pentru care se solicita revalidarea;', ' a efectuat in ultimele 12 luni minimum 3 ore de zbor incluzand 5 decolari si 5 aterizari in calitate de pilot pe clasade aeronave pentru care se solicita revalidarea;', ' a efectuat in ultimele 12 luni minimum 2 ore de zbor incluzand 5 decolari si 5 aterizari in calitate de pilot pe clasade aeronave pentru care se solicita revalidarea.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:17:19'),
(923, 'Legislatie', 23, '      Conform Codului Aerian al Romaniei, art.3.12 , autorizarea reprezinta:', ' confirmarea oficiala data printr-un document eliberat, prin care se atesta capacitatea detinatorului de a desfasura activitatile aeronautice civile mentionate in acest document;', ' recunoasterea faptului ca un serviciu, un produs, o piesa sau un echipament, o organizatie sau o persoana se conformeaza cerintelor aplicabile;', ' document emis ca rezultat al certificarii, in conformitate cu reglementarile specifice aplicabile.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:17:20'),
(924, 'Legislatie', 24, '      Conform Codului Aerian, cap XII scopul investigatiei tehnice in cazul incidentelor si accidentelor de aviatie il reprezinta:', ' stabilirea faptelor, cauzelor si imprejurarilor care au condus la producerea incidentului sau a accidentului de aviatie civila, precum si identificarea masurilor preventive corespunzatoare;', ' cresterea sigurantei zborurilor, prin emiterea de recomandari in vederea prevenirii producerii unor incidente sau accidente similare in aviatia civila;', ' stabilirea responsabilitatilor, a persoanelor vinovate, a gradului de vinovatie, precum si aplicarea sanctiunilor.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:17:20'),
(925, 'Legislatie', 25, '     Planul de zbor reprezinta:', ' o ruta definita si proiectata astfel incat fluxul traficului aerian sa fie orientat in functie de necesitati.', ' o metoda de navigatie care permite operarea unei aeronave pe orice traiectorie dorita, in limitele de acoperire a unor mijloace de navigatie;', ' ansamblu de informatii specifice furnizate serviciilor de trafic cu privire la zborul pe  care o aeronava urmeaza sa-l efectueze.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:17:20'),
(926, 'Legislatie', 26, '    Constituie contraventii, conform Codului Aerian, daca nu au fost savarsite in astfel de conditii incat, potrivit legii, sa constituie infractiuni, urmatoarele fapte:', ' refuzul comandantului unei aeronave civile de a prezenta persoanelor imputernicite, in conditiile legii, documentele obligatorii prevazute la art. 17 alin. (2), art. 36 alin. (2) si la art. 73 alin. (2)', ' neinformarea Ministerului Transporturilor, Constructiilor si Turismului cu privire la producerea incidentelor si accidentelor de aviatie civila, de catre cei care au aceasta obligatie in conformitate cu prevederile art. 90 alin. (2);', ' refuzul comandantului unei aeronave civile de a prezenta persoanelor imputernicite, in conditiile legii, documentele obligatorii prevazute la art. 17 alin. (2), art. 36 alin. (2) si la art. 73 alin. (2).', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2,3', '2015-09-28 16:17:20'),
(927, 'Legislatie', 27, '  NOTAM inseamna:', ' publicatie emisa in numele statului roman sub autoritatea MTI, care contine informatii aeronautice de durata, esentiale pentru navigatia aeriana', ' ansamblu de informatii specifice furnizate unitatilor de trafic aerian cu privire la zborurile ce urmeaza a fi efectuate', ' mesaj de notificare distribuit prin mijloace de telecomunicatii care contine informatii despre aparitia, starea, modificarea, oricarui mijloc serviciu, procedura sau hazard, informatii a caror cunoastere este esentiala pentru zbor.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:17:20'),
(928, 'Legislatie', 27, '  In cazul  reinnoirii licentei de pilot aeronave ultrausoare nemotorizate durata cursului de pregatire teoretica este:', ' 15 ore pentru o intrerupere mai mica de 5 ani;', ' 10 ore pentru o intrerupere mai mica de 5 ani;', ' 25 ore pentru o intrerupere mai mica de 5 ani.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:17:20'),
(929, 'Legislatie', 28, '      Dreptul  de a efectua activitatea de inspectie in teritoriu revine conform legii, inspectorilor autorizati de catre autoritatea de certificare:', ' adevarat;', ' fals.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:17:20'),
(930, 'Legislatie', 29, '     In cadrul procesului de examinare, inspectorul examinator, are obligatia sa verifice carnetul de zbor al persoanei  examinate:', ' fals deoarece doar instructorul este cel care are acces la informatiile din carnetul de zbor.;', ' fals, deoarece fisa de pregatire este documentul care atesta orele de pregatire ale persoanei examinate;', ' adevarat.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:17:20'),
(931, 'Legislatie', 30, '   Spatiu aerian restrictionat este  :', ' portiunea de spatiu aerian in care zborul aeronavelor este interzis', ' portiunea de spatiu aerian care se afla sub responsabilitatea unei autoritati;', ' portiunea de spatiu aerian in care in timpul unor perioade specificate se desfasoara activitati periculoase pentru zbor.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2,3', '2015-09-28 16:17:20'),
(932, 'Legislatie', 31, '    Zborul VFR al unei aeronave civile executat in intregime intr-o zona de spatiu aerian de clasa G se considera autorizat daca:', ' aeronava este asigurata dupa caz cf legii pt daune produse tertilor;', ' operatorul aeronavei a depus plan de zbor, sau a facut informare cf legii;', ' in zona aeriana de clasa G nu este necesar nici un document.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-28 16:20:17'),
(933, 'Legislatie', 32, '   Conform HG.912/2010, controlul utilizarii spatiului aerian se efectueaza de catre:', ' Ministerul Trasporturilor;', ' Ministerul Apararii;', ' organele de trafic civile.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:20:17'),
(934, 'Legislatie', 33, '   Examinarea practica pentru obtinerea/revalidarea si reinoirea calificarilor personalului aeronautic navigant se sustine intotdeauna dupa examinarea teoretica indiferent daca candidatul a obtinut 75% la aceasta:', ' adevarat', ' fals', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:20:17'),
(935, 'Legislatie', 34, '   Operarea unei aeronave civile in spatiul aerian national pentru care nu s-a incheiat asigurare pentru pagube produse tertilor se pedepseste cu amenda de la:', ' 20000- 35000 lei;', ' 40000-50000 lei;', ' 50000 lei.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:20:17'),
(936, 'Legislatie', 35, '    Inaltimea conform RACR-RA este definita ca:', ' distanta verticala unui nivel punct sau obiect, considerat ca punct de la nivelul mediu al marii;', ' distanta verticala unui nivel punct sau obiect, considerat ca punct masurata de la un punct de referinta specificat.', ' distanta verticala unui nivel punct sau obiect, considerat ca punct, masurat de la nivelul 0 al marii.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:20:17'),
(937, 'Legislatie', 36, '   Cererea de licentiere se intocmeste de catre:', ' instructor;', ' examinatorul autorizat;', ' elevul pilot', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:20:17'),
(938, 'Legislatie', 37, '    Intreaga responsabilitate pentru actiunile desfasurate in timpul activitatii  de zbor revine conform legii:', ' pilotului comandant;', ' instructorilor aflati in zona;', ' directorilor de concurs in cazul competitiilor sportive.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:20:17'),
(939, 'Legislatie', 38, '    In timpul procesului de pregatire, elevul pilot desfasoara activitate de zbor numai sub supravegherea instructorului:', ' adevarat, deoarece instructorul este cel care stabileste temele de pregatire conform  programului de pregatire la sol si in zbor ;', ' fals, elevul poate efectua zboruri si in prezenta unor piloti avansati', ' adevarat deoarece instructorul este singura persoana abilitata sa supravegheze activitatea de pregatire la sol si in zbor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,3', '2015-09-28 16:20:17'),
(940, 'Legislatie', 39, '      Autoritatea de certificare poate retrage anula o calificare/autorizatie de aeronave ultrausoare nemotorizate:', ' nu , deoarece o calificare odata obtinuta nu poate fi retrasa;', ' da, daca se constata ca titularul nu mai indeplineste conditiile minime specificate in reglementare;', ' da, daca titularul a fost implicat in actiuni de natura a afecta siguranta zborului', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2,3', '2015-09-28 16:20:17'),
(941, 'Legislatie', 40, '     Zborurile VFR  se pot executa pentru desfasurarea de:', ' operatiuni de transport aerian public;', ' operatiuni de aviatie generala;', ' operatiuni de lucru aerian.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2,3', '2015-09-28 16:22:54'),
(942, 'Legislatie', 41, '   Harta VFR AIP Romania cu indicativul LR-1 corespunde zonei:', ' Muntenia;', ' Transilvania;', ' Moldova.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:22:54'),
(943, 'Legislatie', 42, '   Ce cuprinde art.93 din Codul Aerian?', ' contraventii', ' infractiuni', ' sanctiuni', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:22:54'),
(944, 'Legislatie', 43, '    Harta VFR AIP Romania cu indicativul LR-1 corespunde zonei:', ' Muntenia;', ' Transilvania;', ' Moldova.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:22:55'),
(945, 'Legislatie', 44, ' Carnetul de zbor este obligatoriu in desfasurarea activitatilor de zbor cu aeronave ultrausoare  nemotorizate?', ' da;', ' nu;', ' doar pe perioada de instruire.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:22:55'),
(946, 'Legislatie', 45, ' Documentele necesare unui pilot strain pentru  a putea desfasura activitati de zbor pe teritoriul Romaniei sunt :', ' licenta de pilot in termen de valabilitate;', ' IPPI Card;', ' aviz din partea autoritatii de certificare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-28 16:22:55'),
(947, 'Legislatie', 46, ' Fisa de pregatire teoretica si practica la sol si in zbor se intocmeste de catre :', ' inspectorul examinator', '  instructorul sub supravegehrea caruia s-a finalizat pregatirea', ' elevul pilot, instructorul semneaza doar documentul', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:22:55'),
(948, 'Legislatie', 47, ' Conform OM 630/2007 RACR-LPAN AUN 4010, testele grila in vederea examinarii sunt intocmite de catre:', ' autoritatea de certificare;', ' autoritatea de certificare prin intermediul examinatorilor;', ' autoritatea de certificare prin intermediul instructorilor.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:22:55'),
(949, 'Legislatie', 48, '  Zborurile VFR  se executa ziua:', ' intre rasaritul si apusul soarelui;', ' cu 30 min dupa rasarit, si se incheie cu 30 min inainte de apus', ' dupa noua reglementare HG.912 nu mai exista regula.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:22:55'),
(950, 'Legislatie', 49, '   Actul normativ care stabileste modul si conditiile de  certificare a personalului aeronautic in domeniul aeronavelor ultrausoare nemotorizate este:', ' Codul Aerian;', '  RACR-RA;', '  OM 630/2007.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:22:55'),
(951, 'Legislatie', 50, '    Asociatia de Zbor Liber din Romania este autoritate delegata de:', ' Directia Aviatiei Civile;', ' Autoritatea Aeronautica Civila;', ' Ministerul Transporturilor.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:22:55'),
(952, 'Legislatie', 51, '  Principala autoritate din Romania in domeniul aeronavelor ultrausoare nemotorizate este:', ' AACR- Autoritatea Aeronautica Civila;', ' MTI - Ministerul Transporturilor si Infrastructurii;', ' AZLR - Asociatia de Zbor Liber din Romania;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:26:37'),
(953, 'Legislatie', 52, ' Care este principalul act normativ care reglementeza activitatea de zbor cu aeronave ultrausoare in Romania?', ' OMTCT 630/2007;', ' Legea 399/2005;', ' HG 912/2010', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:26:37'),
(954, 'Legislatie', 53, ' Autoritatea de certificare, daca constata ca detinatorul unei licente nu mai indeplineste conditiile specificate in reglementari:', ' poate suspenda licenta specifica;', ' poate retrage licenta specifica;', ' odata o calificare dobandita ea nu mai poate fi retrasa.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-28 16:26:37'),
(955, 'Legislatie', 54, ' Conform Codului Aerian în vigoare este considerat accident:', '    ranirea grava sau moartea atunci când persoana se afla într-o aeronava', '   producerea unui infarct miocardic în interiorul unei aeronave', '  ranirea unui pasager clandestin aflat în interiorul calei aeronavei', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2015-09-28 16:26:37'),
(956, 'Legislatie', 55, '  Executarea de activiati de zbor cu aeronave ultrausoare se face:', ' cu asumarea responsabilitatilor de catre cei care zboara, în baza unei declaratii tip', ' cu respectarea reglementarilor aeronautice specifice si generale din România', ' cu respectarea reglementarilor specifice, dar numai pentru cetatenii români.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1,2', '2015-09-28 16:26:37'),
(957, 'Legislatie', 56, '  Pentru a putea zbura cu parapanta este necesar:', '  sa-ti cumperi o parapanta', '  sa urmezi o scoala zburând sub îndrumarea instructorului', '  obtinerea licentei de pilot pentru a putea zbura nesupravegheat de un instructor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2,3', '2015-09-28 16:26:37'),
(958, 'Legislatie', 57, '  Zborul de scoala este caracterizat ca fiind:', ' zborul efectuat pentru pregatire în vederea participarii la competitii', ' zborul efectuat în vederea pregatirii pentru depasirea unor recorduri', ' zborul efectuat pentru initiere sau instruire', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:26:37'),
(959, 'Legislatie', 58, '   Spatiul aerian national reprezinta coloana de aer situatã deasupra teritoriului de suveranitate al României, pâna la:', '  10.000 AGL', ' 12.000 ASL', ' limita inferioara a spatiului extraatmosferic', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:26:37'),
(960, 'Legislatie', 59, '    Executarea instruirii cu parapanta se face de catre:', ' orice pilot avansat', ' operatorul radio', '  instructorul licentiat', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', '2015-09-28 16:26:37'),
(961, 'Legislatie', 60, ' Pentru obtinerea licentei de pilot aeronave ultrusoare clasa parapanta elevul pilot trebuie sa parcurga un program de pregatire teoretica ce include un numar total de :', ' 48 ore ;', ' 50 ore ;', ' 48 ore fara  dicipina telefonie', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2015-09-28 16:26:37');
